                        //myFavorites={myFavourites}
                        //clearmyFavourites={clearmyFavourites}
                        ></MyFavouritesPanel>
                    </Grid>    
                
                    <Grid className='results-container' item lg="9.5">
                        <DisplayResults 
                        //keyWord={keyWord}
                        //updatemyFavourites={updatemyFavorites}
                        />
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    );
};

export default Home;
