//import { Home } from '@mui/icons-material';
import './App.css';
import Login from './components/Login';
import Home from './components/Home';
import { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Redirect, Switch } from 'react-router-dom';


function App() {

  //state isLoggedIn should be here so can be passed to Home and Login 
  const [values, setValues] = useState({
    username: "",
    isLoggedIn: false
  });

  useEffect(() => {
    const loggedInStatus = localStorage.getItem('isLoggedIn');
    const storedUsername = localStorage.getItem('username');  
    
    if (loggedInStatus && storedUsername) {
      setValues({...values, isLoggedIn: true});
      setValues({...values, username: storedUsername});
    }
  });



  return (
    /**<div>
      <Router>
        <Routes>
          <Route path="/" {...props} element={<Login/>} />
          <Route path="/home" {...props} element={<Home/>} />
        </Routes>
      </Router> 
    </div> */

    <Router>
      <Switch>
        <Route path="/login">
          {isLoggedIn ? (
            <Redirect to="/home" />
          ) : (
            <Login setIsLoggedIn={setIsLoggedIn} setUsername={setUsername} />
          )}
        </Route>
        <Route path="/home">
          {isLoggedIn ? <Home username={username} /> : <Redirect to="/login" />}
        </Route>
        <Redirect from="/" to="/login" />
      </Switch>
    </Router>
  );
}

export default App;
