import * as React from 'react';
import { Button, IconButton, Input, InputAdornment, TextField } from '@mui/material';
import Grid from '@mui/material/Grid';
import { useState } from 'react';
import { Visibility, VisibilityOff } from '@mui/icons-material';


const Login = () => {

    const [values, setValues] = useState({
        username: "",
        password: "",
        showPassword: false,
        isLoggedIn: false
    });

    const showPasswordHandler = () => {
        setValues({...values, showPassword: !values.showPassword});
    };

    const mouseDownPasswordHandler = (event) => {
        event.preventDefault();
    };

    //TODO understand this 
    const passwordChangeHandler = (prop) => (event) => {
        setValues({...values, [prop]: event.target.value
        });
    };

    //const validatePassword = () => ();

    return(
        <div className='container'>
            <Grid container spacing={2}>
                <Grid item xs={4}>
                </Grid>
                <Grid item xs={4}>
                        <div 
                            className='header' 
                            style={{
                                display: 'flex',
                                justifyContent: 'center',
                                alignItems: 'center',
                                height: '20vh'
                            }}>
                            <h1>Find My News</h1>
                        </div>

                        <div className='inputs'>
                            <div className='input' style={{display: 'flex', justifyContent: 'center', alignItems: 'center', height: '10vh'}}>
                                <TextField id="outlined-basic" label="Username" variant="standard" />
                            </div>
                            <div className='input' style={{display: 'flex', justifyContent: 'center', alignItems: 'center', height: '10vh'}}>
                                <Input
                                    type={values.showPassword ? "text" : "password"} 
                                    placeholder='Password'
                                    size='large'
                                    onChange={passwordChangeHandler("password")}
                                    value={values.password}
                                    endAdornment={
                                        <InputAdornment position="end">
                                            <IconButton
                                                onClick={showPasswordHandler}
                                                onMouseDown={mouseDownPasswordHandler} >
                                                {values.showPassword ? (<Visibility />) : (<VisibilityOff />)}
                                            </IconButton>
                                        </InputAdornment>
                                    }
                                >
                                </Input>
                            </div>
                        </div>

                        <div className='login-container' style={{display: 'flex', justifyContent: 'center', alignItems: 'center', height: '10vh'}}>
                            <Button variant="contained">Log in</Button>
                        </div>
                </Grid>
                <Grid item xs={4}>
                </Grid>
            </Grid>
        </div>
    );
};
export default Login;
